import React, { useState, useEffect } from "react";
import axios from "axios";
import jsPDF from "jspdf";
import "jspdf-autotable";

export default function PartnerDashboard() {
  const [tab, setTab] = useState("list"); // Affichage de la liste des employés par défaut
  const [employes, setEmployes] = useState([]);
  const [nom, setNom] = useState("");
  const [carte, setCarte] = useState("");
  const [rabais, setRabais] = useState(10); // Valeur par défaut à 10%
  const [message, setMessage] = useState("");
  const [totalHT, setTotalHT] = useState(0);
  const [taxe5, setTaxe5] = useState(0);
  const [taxe9, setTaxe9] = useState(0);
  const [totalTTC, setTotalTTC] = useState(0);

  const partenaireId = localStorage.getItem("partenaireId");

  const formatCAD = (value) =>
    new Intl.NumberFormat("fr-CA", {
      style: "currency",
      currency: "CAD",
    }).format(value);

  useEffect(() => {
    if (!partenaireId) {
      window.location.href = "/";
      return;
    }
  }, [partenaireId]);

  useEffect(() => {
    if (tab === "list" && partenaireId) {
      axios
        .get(`http://localhost:5000/api/employee/by-partner/${partenaireId}`)
        .then((res) => setEmployes(res.data || []))
        .catch((err) => console.error("❌ Erreur chargement employés :", err));
    }
  }, [tab, partenaireId]);

  useEffect(() => {
    const baseAmount = 90; // Base cost per employee in CAD
    const subtotal = employes.length * (baseAmount * (rabais / 100)); // Montant total
    const taxTPS = subtotal * 0.05; // 5% TPS
    const taxTVQ = subtotal * 0.09975; // 9.975% TVQ
    const total = subtotal + taxTPS + taxTVQ;

    setTotalHT(subtotal);
    setTaxe5(taxTPS);
    setTaxe9(taxTVQ);
    setTotalTTC(total);
  }, [employes, rabais]);

  const handleAddEmployee = async (e) => {
    e.preventDefault();
    try {
      const totalMontant = employes.length * ((90 * rabais) / 100);
      const payload = {
        partenaireId,
        nom,
        carte,
        rabais,
        montant: totalMontant,
      };

      const response = await axios.post("http://localhost:5000/api/employee/add", payload);
      if (response.status === 200 || response.status === 201) {
        setEmployes([...employes, response.data]);
        setMessage("✅ Employé ajouté !");
        setNom("");
        setCarte("");
        setRabais(10);
      }
    } catch (err) {
      console.error("❌ Erreur ajout :", err);
      setMessage("❌ Erreur lors de l'ajout.");
    }
  };

  const handleDeleteEmployee = async (id) => {
    try {
      console.log("Attempting to delete employee with id:", id); // Log the ID
      const response = await axios.delete(`http://localhost:5000/api/employee/delete/${id}`, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("token") || ""}`,
          "Content-Type": "application/json",
        },
      });
      if (response.status === 200 || response.status === 204) {
        console.log("Delete successful, response:", response.data);
        setMessage("✅ Employé supprimé !");
        setEmployes(employes.filter((emp) => emp._id !== id));
      }
    } catch (err) {
      console.error("❌ Erreur de suppression - Full error:", {
        message: err.message,
        response: err.response ? err.response.data : "No response data",
        status: err.response ? err.response.status : "No status",
        url: err.config ? err.config.url : "Unknown URL",
      });
      setMessage(
        "❌ Erreur lors de la suppression. Vérifiez l'ID ou le serveur. Détails : " +
          (err.response ? `${err.response.status} - ${err.response.statusText}` : err.message)
      );
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("partenaireId");
    window.location.href = "/";
  };

  const exportPDF = () => {
    const doc = new jsPDF();
    doc.text("Liste des employés", 14, 10);
    const rows = employes.map((emp) => [emp.nom, emp.carte, `${emp.rabais} %`]);
    if (rows.length > 0) {
      doc.autoTable({
        head: [["Nom", "Carte", "Rabais"]],
        body: rows,
      });
      doc.save("employes.pdf");
    } else {
      doc.text("Aucun employé à exporter.", 14, 20);
      doc.save("employes.pdf");
    }
  };

  return (
    <div className="min-h-screen bg-cover bg-center relative flex flex-col">
      <div className="absolute inset-0 bg-[#001e3c]/80 z-0" />

      {/* Header */}
      <div className="relative z-10 flex justify-between items-center px-6 py-4">
        <img src="/sts-logo.png" alt="STS" className="h-10" />
        <div className="flex gap-4">
          <h1 className="text-white text-xl font-semibold">Tableau de bord Partenaire</h1>
          <button onClick={handleLogout} className="bg-red-600 text-white px-3 py-1 rounded">
            Déconnexion
          </button>
        </div>
      </div>

      {/* Onglets */}
      <div className="relative z-10 flex justify-center gap-4 py-6">
        <button
          onClick={() => setTab("list")}
          className={`px-4 py-2 rounded ${tab === "list" ? "bg-[#c6d300] text-black" : "bg-white text-[#003865]"}`}
        >
          Liste des employés
        </button>
        <button
          onClick={() => setTab("add")}
          className={`px-4 py-2 rounded ${tab === "add" ? "bg-[#c6d300] text-black" : "bg-white text-[#003865]"}`}
        >
          Inscrire un employé
        </button>
        <button
          onClick={() => setTab("payment")}
          className={`px-4 py-2 rounded ${tab === "payment" ? "bg-[#c6d300] text-black" : "bg-white text-[#003865]"}`}
        >
          Paiement
        </button>
      </div>

      {/* Contenu */}
      <div className="relative z-10 flex-grow px-6 pb-12 flex justify-center items-start">
        <div className="bg-white/95 p-10 rounded-2xl shadow-lg w-full max-w-5xl">
          {/* Liste des employés */}
          {tab === "list" && (
            <div>
              <div className="flex justify-between items-center mb-4">
                <h2 className="text-xl font-bold text-[#003865] text-center">Liste des employés</h2>
                <button onClick={exportPDF} className="bg-red-500 text-white px-3 py-1 rounded">
                  Exporter en PDF
                </button>
              </div>
              <table className="w-full table-auto border border-collapse bg-white">
                <thead className="bg-gray-200">
                  <tr>
                    <th className="border p-2">Nom</th>
                    <th className="border p-2">Carte</th>
                    <th className="border p-2">Rabais</th>
                    <th className="border p-2">Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {employes.length > 0 ? (
                    employes.map((emp) => (
                      <tr key={emp._id}>
                        <td className="border p-2">{emp.nom}</td>
                        <td className="border p-2">{emp.carte}</td>
                        <td className="border p-2">{emp.rabais}%</td>
                        <td className="border p-2">
                          <button
                            onClick={() => handleDeleteEmployee(emp._id)}
                            className="bg-red-500 text-white px-2 py-1 rounded"
                          >
                            Supprimer
                          </button>
                        </td>
                      </tr>
                    ))
                  ) : (
                    <tr>
                      <td colSpan="4" className="text-center p-4 text-gray-500">
                        Aucun employé inscrit
                      </td>
                    </tr>
                  )}
                </tbody>
              </table>
            </div>
          )}

          {/* Formulaire d'ajout d'employé */}
          {tab === "add" && (
            <form className="space-y-4" onSubmit={handleAddEmployee}>
              <h2 className="text-xl font-bold mb-4 text-[#003865] text-center">
                Ajouter un nouvel employé
              </h2>
              <input
                type="text"
                placeholder="Nom et prénom"
                value={nom}
                onChange={(e) => setNom(e.target.value)}
                className="w-full p-3 border rounded"
                required
              />
              <input
                type="text"
                placeholder="Numéro de carte à puce"
                value={carte}
                onChange={(e) => setCarte(e.target.value)}
                className="w-full p-3 border rounded"
                required
              />
              <select
                value={rabais}
                onChange={(e) => setRabais(e.target.value)}
                className="w-full p-3 border rounded"
              >
                <option value="10">10%</option>
                <option value="20">20%</option>
                <option value="30">30%</option>
              </select>
              <button type="submit" className="bg-[#217da0] text-white px-4 py-3 rounded w-full">
                Ajouter
              </button>
              {message && <p className="text-center text-sm text-blue-600">{message}</p>}
            </form>
          )}

          {/* Paiement */}
          {tab === "payment" && (
            <div>
              <h2 className="text-xl font-bold mb-4 text-[#003865] text-center">Paiement</h2>
              <div className="space-y-2 text-gray-700 text-lg">
                <p>Nombre d'employés : <strong>{employes.length}</strong></p>
                <p>Sous-total : <strong>{formatCAD(totalHT)}</strong></p>
                <p>TPS (5%) : <strong>{formatCAD(taxe5)}</strong></p>
                <p>TVQ (9.975%) : <strong>{formatCAD(taxe9)}</strong></p>
                <hr className="my-2" />
                <p>Total : <strong>{formatCAD(totalTTC)}</strong></p>
                <p>Somme exigible : <strong>{formatCAD(totalTTC)}</strong></p>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
