import React, { useState } from "react";
import axios from "axios";

export default function Login() {
  const [isAdmin, setIsAdmin] = useState(false);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [message, setMessage] = useState("");

  // ✅ Fonction API intégrée
  const loginPartenaire = (data) => {
    return axios.post("http://localhost:5000/api/partner/login", data);
  };

  const loginAdmin = (data) => {
    return axios.post("http://localhost:5000/api/admin/login", data);
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const data = { email, password };
      if (isAdmin) {
        const res = await loginAdmin(data);
        localStorage.setItem("adminToken", res.data.token);
        window.location.href = "/dashboard-admin";
      } else {
        const res = await loginPartenaire(data);
        localStorage.setItem("partenaireId", res.data.partenaireId);
        window.location.href = "/dashboard";
      }
    } catch (err) {
      console.error("❌ Login error:", err);
      setMessage("❌ Email ou mot de passe incorrect.");
    }
  };

  return (
    <div className="min-h-screen bg-[#001e3c] flex items-center justify-center px-4">
      <div className="bg-white p-10 rounded-2xl shadow-lg w-full max-w-md">
        <img src="/sts-logo.png" alt="STS" className="h-14 mx-auto mb-6" />
        <h2 className="text-2xl font-bold text-center text-[#003865] mb-6">
          Connexion {isAdmin ? "Admin" : "Partenaire"}
        </h2>

        <form className="space-y-4" onSubmit={handleLogin}>
          <input
            type="email"
            placeholder="Adresse email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
            className="w-full p-3 border rounded"
          />
          <input
            type="password"
            placeholder="Mot de passe"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
            className="w-full p-3 border rounded"
          />

          <div className="flex items-center justify-between">
            <label className="text-sm text-gray-700">
              <input
                type="checkbox"
                checked={isAdmin}
                onChange={(e) => setIsAdmin(e.target.checked)}
                className="mr-2"
              />
              Se connecter comme admin
            </label>
          </div>

          <button
            type="submit"
            className="bg-[#217da0] text-white px-4 py-3 rounded w-full"
          >
            Se connecter
          </button>

          {message && (
            <p className="text-center text-sm text-red-600 mt-2">{message}</p>
          )}
        </form>
      </div>
    </div>
  );
}
