import React, { useEffect, useState } from "react";
import axios from "axios";

export default function AdminDashboard() {
  const [stats, setStats] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:5000/api/admin/stats")
      .then(res => setStats(res.data))
      .catch(err => console.error("❌ Erreur chargement stats :", err));
  }, []);

  return (
    <div className="min-h-screen p-10 bg-gray-100">
      <h1 className="text-3xl font-bold text-[#003865] mb-6">Tableau de bord Admin</h1>
      <table className="w-full bg-white shadow rounded-lg">
        <thead className="bg-[#003865] text-white">
          <tr>
            <th className="p-3 text-left">Entreprise</th>
            <th className="p-3 text-left">Nombre d'employés</th>
          </tr>
        </thead>
        <tbody>
          {stats.map((item, idx) => (
            <tr key={idx} className="border-b">
              <td className="p-3">{item.entreprise}</td>
              <td className="p-3">{item.nbEmployes}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
